import { useRef } from "react";

const canvas = () => {
    const canvasRef = useRef();

    useEffect(() => {
        connect(onGameOver).then(() => {

            playButtonRef.current.onclick = (event) => {
                event.preventDefault();
                play(Username);
                initState();
                startCapturingInput();
                startRendering(canvasRef);
            };
        }).catch(console.error);

        return () => {
            stopCapturingInput();
            stopRendering();
        };
    }, []);

    const onGameOver = () => {
        stopCapturingInput();
        stopRendering();
    };

    return (<canvas ref={canvasRef} id="canvas" className="hidden"></canvas>)
}

export default canvas
