import React, { useState } from 'react';
import axios from 'axios';
import { useSelector } from 'react-redux';
import { getIsLoggedInMemoized, getRoleMemoized } from '../../selectors/selectors';

import LogoutButton from '../../components/Bottuns/LogoutButton/LogoutButton';
import 'bootstrap/dist/css/bootstrap.min.css';
import './admin.css';

const Admin = () => {
    const isLoggedIn = useSelector(getIsLoggedInMemoized);
    const role = useSelector(getRoleMemoized);

    const [showSkinForm, setshowSkinForm] = useState(false);
    const [showUsersForm, setshowUsersForm] = useState(true);
    const [users, setUsers] = useState([]);

    const handleUsersSubmit = async (event) => {
        event.preventDefault()
        const user_name = event.target.value
        if (user_name) {
            await axios.get(`http://localhost:5000/users/${user_name}`, {
                withCredentials: true
            })
                .then(result => {

                    if (result.data) {
                        setUsers(result.data.usernames)
                    }
                    else {
                        alert(result.data.message);
                    }
                })
                .catch(err => console.log(err));
        }
        else { setUsers([]) }
    }
    const handlebanSubmit = async (event, username) => {
        event.preventDefault()
        if (username) {
            await axios.put(`http://localhost:5000/users/${username}/ban`, { reason: "bad" }, {
                withCredentials: true
            })
                .then(result => {
                    if (result.data.message === "User banned") {
                        alert(result.data.message);
                    }
                    else {
                        alert(result.data.message);
                    }
                })
                .catch(err => console.log(err));
        }
    }

    const handleunbanSubmit = async (event, username) => {
        event.preventDefault()
        if (username) {
            await axios.put(`http://localhost:5000/users/${username}/unban`, {}, {
                withCredentials: true
            })
                .then(result => {
                    if (result.data.message === "User Unbanned") {
                        alert(result.data.message);
                    }
                    else {
                        alert(result.data.message);
                    }
                })
                .catch(err => console.log(err));
        }
    }

    const handleDelUserSubmit = async (event, username) => {
        event.preventDefault()
        if (username) {
            await axios.delete(`http://localhost:5000/users/${username}`, {
                withCredentials: true
            })
                .then(result => {
                    if (result.data.message === "user deleted") {
                        alert(result.data.message);
                        const USernames = users.filter(user_name => user_name !== username);
                        setUsers(USernames)
                    }
                    else {
                        alert(result.data.message);
                    }
                })
                .catch(err => console.log(err));
        }
    }

    const handleMakeModerSubmit = async (event, username) => {
        event.preventDefault()
        if (username) {
            await axios.post(`http://localhost:5000/admin/`, { user_name: username }, {
                withCredentials: true
            })
                .then(result => {
                    if (result.data.message === "role changed") {
                        alert(result.data.message);
                    }
                    else {
                        alert(result.data.message);
                    }
                })
                .catch(err => console.log(err));
        }

    }
    const handleGiveCoinSubmit = async (event) => {
        event.preventDefault()
        const user_name = event.target.value

    }

    const handleGiveSkinSubmit = async (event) => {
        event.preventDefault()
        const user_name = event.target.value

    }


    const handleUsersClick = () => {
        setshowUsersForm(true)
        setshowSkinForm(false)
        setUsers([])

    }
    const handleSkinsClick = () => {
        setshowUsersForm(false)
        setshowSkinForm(true)
        setUsers([])

    }



    return (
        <div className="grid-background-panel">
            <div className="position-relative ">
                <div className="container mt-2 ">
                    {!isLoggedIn ? (<> <LogoutButton /></>) : null}
                </div>
            </div>
            <div className="panelWrapper ">
                <div className="flex-column flex-md-row px-3 row containerAdminMain ">
                    <div className="containeright bg-white border mt-1 mt-md-0 ms-md-5 p-5 col-md-9 col-sm-8 col-12 " >

                        <div className="row mt-1 mb-3  text-center ">
                            <div className="containerAdminCols justify-content-between ">
                                <div className="divAdminBttn rounded  ">
                                    <button type="button" className=" list-group-item list-group-item-action" onClick={handleUsersClick}>
                                        Пользователи
                                    </button>

                                </div>
                                <div className="divAdminBttn rounded">
                                    <button type="button" className=" list-group-item list-group-item-action" onClick={handleSkinsClick} >
                                        Ресурсы
                                    </button>
                                </div>
                            </div>
                        </div>
                        {showUsersForm ?
                            <div >
                                <h1 className="Titles-information mt-1">Поиск</h1>
                                <div className="p-0 mt-3">
                                    <label className="FormLabel" htmlFor="ShowUsers">Введите Никнейм</label>
                                    <input
                                        placeholder="Nikcname"
                                        autocomplete="off"
                                        spellcheck="off"
                                        type="text"
                                        className="FormInput px-3 py-2 form-control form-control-md"
                                        onChange={(event) => handleUsersSubmit(event)}
                                    />
                                </div>
                                <ul className="list-group p-0 mt-2">

                                    {users && users.map(user => (
                                        <li className="list-group-item" key={user}>
                                            {user}
                                            <button
                                                className="BtnRemove btn btn-outline-primary"
                                                onClick={(event) => handlebanSubmit(event, user)}>
                                                забанить
                                            </button>

                                            <button
                                                className="BtnRemove btn btn-outline-primary"

                                                onClick={(event) => handleunbanSubmit(event, user)}>
                                                разбанить
                                            </button>
                                            {role === "ADMIN" ?
                                                <button
                                                    className="BtnRemove btn btn-outline-primary"

                                                    onClick={(event) => handleDelUserSubmit(event, user)}>
                                                    удалить
                                                </button> : null}

                                        </li>))}
                                </ul>

                            </div>
                            : null
                        }
                        {showSkinForm ?
                            <div >
                                <h1 className="Titles-information mt-1">Поиск</h1>
                                <div className="p-0 mt-3">

                                    <label className="FormLabel" htmlFor="ShowUsers">Введите Никнейм</label>
                                    <input
                                        placeholder="Nikcname"
                                        autocomplete="off"
                                        spellcheck="off"
                                        type="text"
                                        className="FormInput px-3 py-2 form-control form-control-md"
                                        onChange={(event) => handleUsersSubmit(event)}
                                    />
                                </div>
                                <ul className="list-group p-0 mt-2">
                                    {role === "ADMIN" && users && users.map(user => (
                                        <li className="list-group-item" key={user}>
                                            {user}
                                            <div className="mt-3">

                                                <button
                                                    className="BtnAdminRemove btn btn-outline-primary"

                                                    onClick={(event) => handleMakeModerSubmit(event, user)}>
                                                    Сделать модератором
                                                </button>
                                                <button
                                                    className="BtnAdminRemove  btn btn-outline-primary"

                                                    onClick={() => true}>
                                                    Выдать Коины
                                                </button>
                                                <button
                                                    className="BtnAdminRemove btn btn-outline-primary"

                                                    onClick={(event) => handleGiveSkinSubmit(event, user)}>
                                                    Выдать скин
                                                </button> :
                                            </div>
                                        </li>))}
                                </ul>


                            </div>
                            : null}
                    </div>
                </div>
            </div>
        </div>
    );

}

export default Admin

