import React from "react";

export interface IGameContextProps {
    isGameStarted: boolean;
    setGameStarted: (started: boolean) => void;
}

const defaultState: IGameContextProps = {
    isGameStarted: false,
    setGameStarted: () => { },
};

export default React.createContext(defaultState);
