import React, { useState, useEffect } from 'react';
import { Routes, Route, Navigate } from 'react-router-dom';
import { useDispatch, useSelector } from 'react-redux';
import { setAuthState, setRole } from './actions/actions';
import { checkAuthStatus } from "./utils/checkAuthStatus";
import { getIsLoggedInMemoized, getRoleMemoized } from './selectors/selectors';
import Login from './pages/login/Login';
import Register from './pages/register/Register';
import Play from './pages/play/play';
import Account from './pages/account/account';
import Shop from './pages/shop/shop';
import Admin from './pages/admin/admin';
import './App.css';




function App() {
  const [isLoading, setIsLoading] = useState(true);
  const isLoggedIn = useSelector(getIsLoggedInMemoized);
  const role = useSelector(getRoleMemoized);
  const dispatch = useDispatch();


  useEffect(() => {
    async function fetchAuthStatus() {
      try {
        const { isLoggedIn, role } = await checkAuthStatus();
        dispatch(setAuthState(isLoggedIn));        
        dispatch(setRole(role));
        setIsLoading(false);
      } catch (error) {
        setIsLoading(false);
      }
    }

    fetchAuthStatus();
  }, [dispatch]);


  return isLoading ? (
    <div className="grid-background"></div>
  ) : (
    <Routes>
      <Route path="/" exact element={<Play />} />
      <Route path="/account" element={isLoggedIn ? <Account /> : <Navigate to="/login" />} />
      <Route path="/register" element={!isLoggedIn ? <Register /> : <Navigate to="/" />} />
      <Route path="/login" element={!isLoggedIn ? <Login /> : <Navigate to="/" />} />
      <Route path="/admin" element={isLoggedIn && (role === "ADMIN" || role === "MODERATOR") ? <Admin /> : <Navigate to="/" />} />
      <Route path="*" element={<Navigate to="/" />} />
    </Routes> 
  );
}

export default App;
