import { useState, useEffect, useRef } from "react";
import { connect, play } from "../../game/networking";
import { startCapturingInput, stopCapturingInput } from "../../game/input";
import { initState } from "../../game/state";
import { startRendering, stopRendering } from "../../game/render";

import 'bootstrap/dist/css/bootstrap.min.css';
import './play.css'


const Play = () => {

  const [Username, setUsername] = useState();
  const playMenuRef = useRef(null);
  const playButtonRef = useRef(null);

  useEffect(() => {
    connect(onGameOver).then(() => {
      playMenuRef.current.classList.remove("hidden");

      playButtonRef.current.onclick = (event) => {
        event.preventDefault();
        play(Username);
        playMenuRef.current.classList.add("hidden");
        canvasRef.current.classList.remove("hidden");
        initState();
        startCapturingInput();
        startRendering(canvasRef);
      };
    }).catch(console.error);

    return () => {
      stopCapturingInput();
      stopRendering();
    };
  }, []);

  const onGameOver = () => {
    stopCapturingInput();
    stopRendering();
    playMenuRef.current.classList.remove("hidden");
    canvasRef.current.classList.add("hidden");
  };


  return (

    <div>
      <div ref={playMenuRef} className="grid-background hidden" >
        <div className="wrapper" >
          <input
            className=" wrap-input"
            type="text"
            placeholder="Nickname"
            maxlength="24"
            onChange={(event) => setUsername(event.target.value)}
            required

          />
          <button type="submit" ref={playButtonRef} className="bttn">Play</button>
        </div>
      </div>
    </div>
  )
}

export default Play


