const initialState = {
    auth: false,
    role: ''
  };

  const reducer = (state = initialState, action) => {
    switch (action.type) {
      case 'SET_AUTH_STATE':
        return {
          ...state,
          auth: action.payload
        };
      default:
        return state;
    }
  };

  export default reducer;
