import { useState } from 'react';
import { Link } from "react-router-dom";
import axios from 'axios';

import 'bootstrap/dist/css/bootstrap.min.css';
import './login.css';

const Login = () => {
    const [email, setEmail] = useState();
    const [password, setPassword] = useState();

    const handleSubmit = async () => {

        if (!email || !password) return;

        await axios.post('http://localhost:5000/auth/login', { email, password }, {
            withCredentials: true
        })
            .then(result => {
                console.log(result);
                if (result.data.message === "You have successfully logged in") {
                    alert('nice');
                }
                else {
                    alert('Incorrect password! Please try again.');
                }
            })
            .catch(err => console.log(err));
    }


    return (
        <div className="grid-background">
            <div className="d-flex justify-content-center align-items-center  vh-100" >
                <div className=" bg-white border mt-md-0 ms-md-5 p-5 col-md-7 col-sm-8 col-1 p-4 rounded" style={{ width: '30%', height: '70%' }}>
                    <h1 className="Titles">Login</h1>
                    <form className="mt-3" onSubmit={handleSubmit}>
                        <div className=" mt-2 text-start">
                            <label htmlFor="exampleInputEmail1" className="FormLabel">
                                Email
                            </label>
                            <input
                                type="email"
                                placeholder="Enter Email"
                                className="form-control px-3 py-2 form-control-md"
                                id="exampleInputEmail1"
                                onChange={(event) => setEmail(event.target.value)}
                                required
                            />
                        </div>
                        <div className="mt-2 text-start">
                            <label htmlFor="exampleInputPassword1" className="FormLabel">
                                Password
                            </label>
                            <input
                                type="password"
                                placeholder="Enter Password"
                                className="form-control px-3 py-2 form-control-md"
                                id="exampleInputPassword1"
                                onChange={(event) => setPassword(event.target.value)}
                                required
                            />
                        </div>
                        <div className="mt-4 text-center">

                            <div>
                                <button type="submit" style={{ width: '30%' }} className=" btn btn-primary">Login</button>
                            </div>
                            <p className='mt-2 my-2'>Dont have an account ?</p>
                            <div>
                                <Link to='/' style={{ width: '30%' }} className=" mt-1 btn btn-outline-primary">back</Link>
                            </div>
                        </div>
                    </form>
                </div>
            </div>
        </div>
    )
}

export default Login
