import axios from 'axios';
import React, { useState, useEffect } from 'react';
import { Link } from "react-router-dom";
import { useSelector } from 'react-redux';
import { getIsLoggedInMemoized } from '../../selectors/selectors';
import LogoutButton from '../../components/Bottuns/LogoutButton/LogoutButton';
import 'bootstrap/dist/css/bootstrap.min.css';
import './account.css';

const Account = () => {
    const isLoggedIn = useSelector(getIsLoggedInMemoized);

    const [showSkinForm, setshowSkinForm] = useState(false);
    const [showChangePasswordForm, setShowChangePasswordForm] = useState(true);
    const [showChangeNameForm, setShowChangeNameForm] = useState(false);
    const [showUsersForm, setshowUsersForm] = useState(false);
    const [showStatisticsForm, setShowStatisticsForm] = useState(false);
    const [showFriendForm, setShowFriendForm] = useState(false);
    const [new_username, setUsername] = useState();
    const [Achievements, setAchievements] = useState([]);
    const [oldPassword, setOldPassword] = useState();
    const [newPassword, setNewPassword] = useState();
    const [friends, setFriends] = useState([]);
    const [users, setUsers] = useState([]);
    const [User_name, setUser_name] = useState('');
    const [Email, setEmail] = useState('');
    const [createdAt, setCreatedAt] = useState('');
    const [Skins, setSkins] = useState([]);

    const [Playtime, setPlaytime] = useState(0);
    const [CountGames, setCountGames] = useState(0);
    const [CountSkins, setCountSkins] = useState(1);

    useEffect(() => {
        const fetchData = async () => {
            try {
                const result = await axios.get(`http://localhost:5000/account/info`, {
                    withCredentials: true
                })
                if (result.data.message === "Request is successfully") {
                    setUser_name(result.data.info.user_name)
                    setEmail(result.data.info.email)
                    setCreatedAt(result.data.info.createdAt.substring(0, 10))
                }
            } catch (error) {
                console.log(error);
            }
        };

        fetchData();
    }, []);

    const handlePassSubmit = async (event) => {
        event.preventDefault()
        if (!oldPassword || !newPassword) return;

        await axios.post('http://localhost:5000/account/changepassword', { oldPassword, newPassword }, {
            withCredentials: true
        })
            .then(result => {
                console.log(result);
                if (result.data.message === "Password changed successfully") {
                    alert('nice');
                }
                else {
                    alert(result.data.message);
                }
            })
            .catch(err => console.log(err));
    }

    const handleNickSubmit = async (event) => {
        event.preventDefault()
        if (!new_username) return;

        await axios.post('http://localhost:5000/account/changenickname', { new_username }, {
            withCredentials: true
        })
            .then(result => {
                console.log(result);
                if (result.data.message === "The nickname has been changed") {
                    alert('nice');
                }
                else {
                    alert(result.data.message);
                }
            })
            .catch(err => console.log(err));
    }
    const handlefriendSubmit = async (event) => {
        event.preventDefault()
        await axios.get('http://localhost:5000/account/friends', {
            withCredentials: true
        })
            .then(result => {
                console.log(result);
                if (result.data.message === "Request is successfully") {
                    setFriends(result.data.friends)
                }
                else {
                    alert(result.data.message);
                }
            })
            .catch(err => console.log(err));
    }

    const handleEquip = async (event, skin_id) => {
        event.preventDefault()
        await axios.post(`http://localhost:5000/account/skins/equip`, { skin_id }, {
            withCredentials: true
        })
            .then(result => {
                console.log(result);
                if (result.data.message === "successfully") {
                    handleSkinSubmit(event)
                }
            })
            .catch(err => console.log(err));
    }
    const handleRemoveFriend = async (event, user_name) => {
        event.preventDefault()
        await axios.delete(`http://localhost:5000/account/friends/${user_name}`, {
            withCredentials: true
        })
            .then(result => {
                console.log(result);
                if (result.data.message === "successfully deleted") {
                    handlefriendSubmit(event)
                }
                else {
                    alert(result.data.message);
                }
            })
            .catch(err => console.log(err));
    }
    const handleAddFriend = async (event, username) => {
        event.preventDefault()

        await axios.post(`http://localhost:5000/account/friends/${username}`, {}, {
            withCredentials: true
        })
            .then(result => {
                console.log(result);
                if (result.data.message === "successfully added") {
                    handlefriendSubmit(event)
                }
                else {
                    alert(result.data.message);
                }
            })
            .catch(err => console.log(err));
    }

    const handleUsersSubmit = async (event) => {
        event.preventDefault()
        const user_name = event.target.value
        if (user_name) {
            await axios.get(`http://localhost:5000/users/${user_name}`, {
                withCredentials: true
            })
                .then(result => {
                    if (result.data) {
                        const USernames = result.data.usernames.filter(username => username !== User_name);
                        setUsers(USernames)
                        handlefriendSubmit(event)
                    }
                    else {
                        alert(result.data.message);
                    }
                })
                .catch(err => console.log(err));
        }
        else { setUsers([]) }
    }

    const handleSkinSubmit = async (event) => {
        event.preventDefault()
        await axios.get(`http://localhost:5000/account/skins`, {
            withCredentials: true
        })
            .then(result => {
                if (result.data.message === "Request is successfully") {
                    setSkins(result.data.ownedSkins)
                }
            })
            .catch(err => console.log(err));

    }
    const handleStatSubmit = async (event) => {
        event.preventDefault()
        await axios.get(`http://localhost:5000/stats/${User_name}`, {
            withCredentials: true
        })
            .then(result => {
                if (result.data.message === "Request is successfully") {
                    setPlaytime(result.data.stats.Playtime)
                    setCountGames(result.data.stats.countgames)
                    setCountSkins(result.data.stats.countskins)
                    console.log(result.data)
                }
            })
            .catch(err => console.log(err));
        await axios.get(`http://localhost:5000/stats/${User_name}/achievements`, {
            withCredentials: true
        })
            .then(result => {
                if (result.data.message === "Request is successfully") {
                    setAchievements(result.data.UserAchievements)
                }
            })
            .catch(err => console.log(err));

    }
    const handlePasswordClick = () => {
        setShowChangePasswordForm(true);
        setShowChangeNameForm(false);
        setShowStatisticsForm(false);
        setShowFriendForm(false)
        setshowUsersForm(false)
        setshowSkinForm(false)
    }

    const handleNameClick = () => {
        setShowChangePasswordForm(false);
        setShowChangeNameForm(true);
        setShowStatisticsForm(false);
        setShowFriendForm(false)
        setshowUsersForm(false)
        setshowSkinForm(false)
    }

    const handleStatisticsClick = async (event) => {
        setShowChangePasswordForm(false);
        setShowChangeNameForm(false);
        setShowFriendForm(false)
        setShowStatisticsForm(true);
        setshowUsersForm(false)
        setshowSkinForm(false)
        await handleStatSubmit(event);

    }
    const handleFriendClick = async (event) => {
        setShowChangePasswordForm(false);
        setShowChangeNameForm(false);
        setShowStatisticsForm(false);
        setShowFriendForm(true)
        setshowUsersForm(false)
        setshowSkinForm(false)
        await handlefriendSubmit(event);

    }
    const handleUsersClick = () => {
        setShowChangePasswordForm(false);
        setShowChangeNameForm(false);
        setShowStatisticsForm(false);
        setShowFriendForm(false)
        setshowUsersForm(true)
        setshowSkinForm(false)

    }
    const handleSkinsClick = async (event) => {
        setShowChangePasswordForm(false);
        setShowChangeNameForm(false);
        setShowStatisticsForm(false);
        setShowFriendForm(false)
        setshowUsersForm(false)
        setshowSkinForm(true)
        await handleSkinSubmit(event)
    }



    return (
        <div className="grid-background-panel">
            <div className="position-relative ">
                <div className="container mt-2 ">
                    {!isLoggedIn ? (<> <LogoutButton /></>) : null}
                </div>
            </div>
            <div className="panelWrapper ">
                <div className="flex-column flex-md-row px-3 row containerMain ">
                    <div className="containerLeft d-flex flex-column justify-content-center p-0 col-md-4 col-sm-8 col-12">
                        <div className="Profilecontainer  d-flex flex-column  border bg-white align-items-center text-center">
                            <h1 className="UserProfile-username mt-3">{User_name}</h1>
                            <h4 className="UserProfile-createdAt mt-1">Since {createdAt}</h4>
                            <h4 className="UserProfile-email mt-1">{Email}</h4>
                        </div>
                        <div className="containerNavbar  mt-4 bg-white border px-2 py-4">
                            <div className="row mt-3 align-items-center text-center ">
                                <div className="col-3 containerCols">
                                    <div className="list-group ">
                                        <button type="button" className="list-group-item list-group-item-action" onClick={handlePasswordClick}>Изменить пароль</button>
                                        <button type="button" className="list-group-item list-group-item-action" onClick={handleNameClick}>Изменить имя</button>
                                        <button type="button" className="list-group-item list-group-item-action" onClick={handleStatisticsClick}>Статистика</button>
                                        <button type="button" className="list-group-item list-group-item-action" onClick={(event) => handleFriendClick(event)}>Друзья</button>
                                        <button type="button" className="list-group-item list-group-item-action" onClick={handleUsersClick}>Поиск</button>
                                        <button type="button" className="list-group-item list-group-item-action" onClick={handleSkinsClick} >Скины</button>
                                    </div>
                                </div>
                            </div>
                        </div>
                    </div>
                    <div className="containeright bg-white border mt-5 mt-md-0 ms-md-5 p-5 col-md-7 col-sm-8 col-12" >
                        {showChangePasswordForm ?
                            <div>
                                <h1 className="Titles-information mt-1">Изменить пароль</h1>
                                <form className="mt-4" onSubmit={handlePassSubmit}>
                                    <div className="p-0">
                                        <label className="FormLabel" htmlFor="oldPassword" >Старый пароль</label>
                                        <input
                                            placeholder="Enter your Current Password"
                                            autocomplete="off"
                                            spellcheck="off"
                                            type="password"
                                            className="FormInput px-3 py-2 form-control form-control-md"
                                            onChange={(event) => setOldPassword(event.target.value)}
                                        />
                                    </div>
                                    <div className="p-0 mt-4">
                                        <label className="FormLabel" htmlFor="newPassword">Новый пароль</label>
                                        <input
                                            placeholder="Enter your New Password"
                                            autocomplete="off"
                                            spellcheck="off"
                                            type="password"
                                            className="FormInput px-3 py-2 form-control form-control-md"
                                            onChange={(event) => setNewPassword(event.target.value)}
                                        />
                                    </div>
                                    <button type="submit" className="mt-5 py-2 px-4 btn btn-primary">Изменить пароль</button>
                                </form>
                            </div> : null}

                        {showChangeNameForm ?
                            <div>
                                <h1 className="Titles-information mt-1">Изменить никнейм</h1>
                                <form className="mt-4" onSubmit={handleNickSubmit}>
                                    <div className="form-group">
                                        <label className="FormLabel" htmlFor="newName">Новый никнейм</label>
                                        <input
                                            placeholder="Enter your Nickname"
                                            autocomplete="off"
                                            spellcheck="off"
                                            type="text"
                                            className="FormInput px-3 py-2 form-control form-control-md"
                                            onChange={(event) => setUsername(event.target.value)}
                                        />
                                    </div>
                                    <button type="submit" className="mt-3 py-2 px-4 btn btn-primary">Изменить никнейм</button>
                                </form></div> : null}
                        {showStatisticsForm ?
                            <div >
                                <h1 className="Titles-information mt-1">Статистика</h1>
                                <div className="row ">
                                    <div className="col-sm-4">
                                        <div className="leaderboard-card">
                                            <div className="leaderboard-card__top">
                                                <h3 className="text-center">{CountGames}</h3>
                                            </div>
                                            <div className="leaderboard-card__body">
                                                <div className="text-center">
                                                    <h5 className="mb-0 mt-3">Games Played</h5>
                                                    <p className="text-muted mb-0"></p>
                                                </div>
                                            </div>
                                        </div>
                                    </div>
                                    <div className="col-sm-4">
                                        <div className="leaderboard-card">
                                            <div className="leaderboard-card__top">
                                                <h3 className="text-center">{Playtime}</h3>
                                            </div>
                                            <div className="leaderboard-card__body">
                                                <div className="text-center">
                                                    <h5 className="mb-0 mt-3">Playtime</h5>
                                                    <p className="text-muted mb-0"></p>
                                                </div>
                                            </div>
                                        </div>
                                    </div>
                                    <div className="col-sm-4">
                                        <div className="leaderboard-card">
                                            <div className="leaderboard-card__top">
                                                <h3 className="text-center">{CountSkins}</h3>
                                            </div>
                                            <div className="leaderboard-card__body">
                                                <div className="text-center">
                                                    <h5 className="mb-0 mt-5">Skins Owned</h5>
                                                    <p className="text-muted mb-0"></p>
                                                </div>
                                            </div>
                                        </div>
                                    </div>
                                </div>
                                <div className='row'>
                                    <h1 className="Titles-information text-center">Достижения</h1>
                                    <ul className="list-group text-center p-0 mt-1">
                                        {Achievements.map(Achievement => (
                                            <li key={Achievement} className="list-group-item">{Achievement.name}</li>
                                        ))}
                                    </ul>
                                </div>
                            </div>
                            : null
                        }
                        {showFriendForm ?
                            <div >
                                <h1 className="Titles-information mt-1">Друзья</h1>
                                <ul className="list-group mt-4 ">

                                    {friends.map(friend => (
                                        <li key={friend} className="list-group-item">
                                            {friend}
                                            <button className="BtnRemove btn btn-outline-primary" onClick={(event) => handleRemoveFriend(event, friend)}>
                                                Удалить
                                            </button>
                                        </li>
                                    ))}
                                </ul>
                            </div>
                            : null
                        }
                        {showUsersForm ?
                            <div >
                                <h1 className="Titles-information mt-1">Поиск</h1>
                                <div className="p-0 mt-4">
                                    <label className="FormLabel" htmlFor="ShowUsers">Введите Никнейм</label>
                                    <input
                                        placeholder="Nikcname"
                                        autocomplete="off"
                                        spellcheck="off"
                                        type="text"
                                        className="FormInput px-3 py-2 form-control form-control-md"
                                        onChange={(event) => handleUsersSubmit(event)}
                                    />
                                </div>
                                <ul className="list-group p-0 mt-2">
                                    {users && users.map(user => (
                                        <li className="list-group-item" key={user}>
                                            {user}
                                            <button
                                                className="BtnRemove btn btn-outline-primary"
                                                disabled={friends.includes(user)}
                                                onClick={(event) => handleAddFriend(event, user)}>
                                                {friends.includes(user) ? 'добавлен' : 'добавить'}
                                            </button>
                                        </li>
                                    ))}
                                </ul>

                            </div>
                            : null
                        }
                        {showSkinForm ?
                            <div >
                                <h1 className="Titles-information mt-1">Скины</h1>
                                <div className="p-0 mt-3">
                                    <ul className="cards">
                                        {Skins && Skins.map(skin => (
                                            <li className="cards_item" key={skin.id}>
                                                <div className="card">
                                                    <div className="card_image"><center>
                                                        <img src={require('../../assets/player.png')}></img>
                                                    </center>
                                                    </div>
                                                    <div className="card_content">
                                                        <h2 className="card_title">{skin.name}<i className="fa-solid fa-circle-xmark"></i></h2>

                                                        <button
                                                            className="btn_card mt-2" skin="player"
                                                            disabled={skin.equipped}
                                                            onClick={(event) => handleEquip(event, skin.id)}
                                                        > {skin.equipped ? 'EQUIPPID' : 'EQUIP'}</button>
                                                    </div>
                                                </div>
                                            </li>
                                        ))}
                                    </ul>
                                </div>


                            </div>
                            : null
                        }

                    </div>
                </div>
            </div>
        </div>
    );

}

export default Account
