import React, { useState, useEffect } from 'react';
import { Routes, Route, Navigate } from 'react-router-dom';
import { useDispatch, useSelector } from 'react-redux';
import { setAuthState } from './actions/actions';
import { checkAuthStatus } from "./Services/checkAuthStatus";
import { getIsLoggedInMemoized } from './selectors/selectors';
import Play from './pages/play/play';
import './App.css';




function App() {
  const [isLoading, setIsLoading] = useState(true);
  const isLoggedIn = useSelector(getIsLoggedInMemoized);
  const dispatch = useDispatch();


  useEffect(() => {
    async function fetchAuthStatus() {
      try {
        const { isLoggedIn, role } = await checkAuthStatus();
        dispatch(setAuthState(isLoggedIn));        
        setIsLoading(false);
      } catch (error) {
        setIsLoading(false);
      }
    }

    fetchAuthStatus();
  }, [dispatch]);


  return isLoading ? (
    <div className="grid-background"></div>
  ) : (
    <Routes>
      <Route path="/" exact element={<Play />} />
      <Route path="*" element={<Navigate to="/" />} />
    </Routes> 
  );
}

export default App;
