import { useState,useEffect, useRef } from "react";
import { connect, play } from "../../game/networking";
import { startCapturingInput, stopCapturingInput } from "../../game/input";
import { initState } from "../../game/state";
import { startRendering, stopRendering } from "../../game/render";

import 'bootstrap/dist/css/bootstrap.min.css';
import './play.css'


const Play = () => {

    const [Username, setUsername] = useState();
    const playMenuRef = useRef(null);
    const playButtonRef = useRef(null);
    const canvasRef = useRef();

    useEffect(() => {
      connect(onGameOver).then(() => {
        playMenuRef.current.classList.remove("hidden");

        playButtonRef.current.onclick = (event) => {
          event.preventDefault();
          play(Username);
          playMenuRef.current.classList.add("hidden");
          canvasRef.current.classList.remove("hidden");
          initState();
          startCapturingInput();
          startRendering(canvasRef);
        };
      }).catch(console.error);

      return () => {
        stopCapturingInput();
        stopRendering();
      };
    }, []);

    const onGameOver = () => {
      stopCapturingInput();
      stopRendering();
      playMenuRef.current.classList.remove("hidden");
      canvasRef.current.classList.add("hidden");
    };


    return (


            <div ref={playMenuRef} className="grid-background hidden" >
                <div className="d-flex justify-content-center align-items-center text-center vh-100" >

                    <div className="bg-white p-3 rounded  bg-transparent" style={{ width: '16%', height: '45%', maxWidth: '500px' }} >
                        <form>

                            <div className="mb-3 mt-3 text-start">

                                <input
                                    type="text"
                                    placeholder="Enter Name"
                                    className=" form-control rounded-start rounded-end"
                                    id="exampleInputname"
                                    onChange={(event) => setUsername(event.target.value)}
                                    required


                                />
                            </div>
                            <div className="center-container">
                                <button type="submit" ref={playButtonRef} id="play-button" className=" bttn third" style={{ width: '40%' }}
                                >Play</button>
                            </div>
                        </form>
                    </div>
                </div>
            <canvas ref={canvasRef} id="canvas" className="hidden"></canvas>
        </div>
    )
}

export default Play


