import { useState, useEffect, useRef } from "react";
import 'bootstrap/dist/css/bootstrap.min.css';
import './play.css'
import Canvas from "./../../components/canvas/canvas"

const GameContext = createContext(null);

const Play = () => {

  const [Username, setUsername] = useState();
  const [Gamestarted, setGamestarted] = useState(false);
  return (

    <div>
      {!Gamestarted ?
        <div className="grid-background hidden" >
          <div className="wrapper" >
            <input
              className=" wrap-input"
              type="text"
              placeholder="Nickname"
              maxlength="24"
              onChange={(event) => setUsername(event.target.value)}
              required
            />
            <button type="submit" onclick={() => setGamestarted(true)} className="bttn">Play</button>
          </div>
        </div>
        : <GameContext.Provider value={{ Gamestarted, Username, setUsername, setGamestarted }}> <Canvas /> </GameContext.Provider>}
    </div>
  )
}

export default Play


